import React, { useEffect, useState } from 'react';
import '../styles/GlobalStyles.css';

const sessionListStyles = {
  container: {
    minHeight: '100vh',
    background: '#111',
    color: '#fff',
    padding: '2rem',
  },
  card: {
    background: '#222',
    borderRadius: '8px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.5)',
    padding: '1.5rem',
    marginBottom: '1.5rem',
    maxWidth: '600px',
    marginLeft: 'auto',
    marginRight: 'auto',
    position: 'relative',
  },
  title: { fontSize: '1.1rem', fontWeight: 'bold', marginBottom: '0.5rem' },
  meta: { color: '#aaa', fontSize: '0.95rem', marginBottom: '0.5rem' },
  status: { fontWeight: 'bold', marginTop: '0.5rem', color: '#0d6efd' },
  actionButton: {
    background: '#0d6efd',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginRight: '1rem',
    marginTop: '1rem',
  },
  cancelButton: {
    background: '#ff4d4f',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginTop: '1rem',
  },
  input: {
    width: '100%',
    padding: '0.5rem',
    borderRadius: '4px',
    border: '1px solid #444',
    background: '#181818',
    color: '#fff',
    fontSize: '1rem',
    marginTop: '0.5rem',
  },
};

function AddSessionForm({ onSessionAdded }) {
  const [skillId, setSkillId] = useState('');
  const [scheduledTime, setScheduledTime] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    const token = localStorage.getItem('token');
    if (!token) return setError('You must be logged in.');

    try {
      const res = await fetch('http://localhost:5001/api/sessions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ skillId, scheduledTime }),
      });
      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.message || 'Failed to create session');
      }
      const data = await res.json();
      onSessionAdded(data);
      setSkillId('');
      setScheduledTime('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div style={{ marginBottom: '2rem', maxWidth: '600px', margin: '0 auto' }}>
      <h3 style={{ marginBottom: '1rem', color: '#fff' }}>Add New Session</h3>
      {error && <div style={{ color: '#ff4d4f', marginBottom: '0.5rem' }}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <input
          style={sessionListStyles.input}
          type="text"
          placeholder="Skill ID"
          value={skillId}
          onChange={(e) => setSkillId(e.target.value)}
        />
        <input
          style={sessionListStyles.input}
          type="datetime-local"
          value={scheduledTime}
          onChange={(e) => setScheduledTime(e.target.value)}
        />
        <button type="submit" style={sessionListStyles.actionButton}>
          Add Session
        </button>
      </form>
    </div>
  );
}

function SessionList() {
  const [sessionList, setSessionList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');
  const [rescheduleId, setRescheduleId] = useState(null);
  const [newTime, setNewTime] = useState('');
  const [reviewedSessionIds, setReviewedSessionIds] = useState([]);
  const [reviewRating, setReviewRating] = useState(5);
  const [reviewComment, setReviewComment] = useState('');
  const [reviewError, setReviewError] = useState('');
  const [reviewSuccess, setReviewSuccess] = useState('');

  const fetchSessions = async () => {
    setIsLoading(true);
    setErrorMessage('');
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to view sessions.');
      setIsLoading(false);
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/sessions', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) throw new Error('Failed to fetch sessions');
      const data = await response.json();
      setSessionList(data);
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchMyReviews = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const response = await fetch('http://localhost:5001/api/reviews/mine', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) return;
      const data = await response.json();
      setReviewedSessionIds(data.map((r) => r.session));
    } catch {}
  };

  useEffect(() => {
    fetchSessions();
    fetchMyReviews();
  }, []);

  const handleUpdateSession = async (sessionId, update) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to update sessions.');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/sessions/${sessionId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(update),
      });
      if (!response.ok) throw new Error('Failed to update session');
      setRescheduleId(null);
      setNewTime('');
      fetchSessions();
    } catch (err) {
      setErrorMessage(err.message);
    }
  };

  const handleReviewSubmit = async (session) => {
    setReviewError('');
    setReviewSuccess('');
    const token = localStorage.getItem('token');
    if (!token) {
      setReviewError('You must be logged in to leave a review.');
      return;
    }
    const userId = JSON.parse(atob(token.split('.')[1])).userId;
    const reviewee = session.owner._id === userId ? session.requester._id : session.owner._id;
    try {
      const response = await fetch('http://localhost:5001/api/reviews', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ reviewee, session: session._id, rating: reviewRating, comment: reviewComment }),
      });
      if (!response.ok) {
        const data = await response.json();
        setReviewError(data.message || 'Failed to submit review');
        return;
      }
      setReviewSuccess('Review submitted!');
      setReviewedSessionIds([...reviewedSessionIds, session._id]);
      setReviewRating(5);
      setReviewComment('');
    } catch {
      setReviewError('Failed to submit review');
    }
  };

  return (
    <div className="black-bg-page">
      <div className="particles">
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
      </div>
      <div className="page-content">
        <div className="black-card">
          <h2 style={{ textAlign: 'center', marginBottom: '2rem', color: '#fff' }}>My Sessions</h2>
          <AddSessionForm onSessionAdded={(newSession) => setSessionList([...sessionList, newSession])} />
          {isLoading && <div>Loading...</div>}
          {errorMessage && <div style={{ color: '#ff4d4f', textAlign: 'center' }}>{errorMessage}</div>}
          {sessionList.map((session) => (
            <div key={session._id} className="black-card" style={{ marginBottom: '1.5rem' }}>
              <div style={sessionListStyles.title}>Skill: {session.skill?.title}</div>
              <div style={sessionListStyles.meta}>
                With: {session.owner?.name} & {session.requester?.name}
                <br />
                Scheduled Time: {new Date(session.scheduledTime).toLocaleString()}
                <br />
                Status: <span style={sessionListStyles.status}>{session.status}</span>
              </div>

              {rescheduleId === session._id ? (
                <>
                  <input
                    style={sessionListStyles.input}
                    type="datetime-local"
                    value={newTime}
                    onChange={(e) => setNewTime(e.target.value)}
                  />
                  <button
                    className="black-btn"
                    onClick={() => handleUpdateSession(session._id, { scheduledTime: newTime, status: 'rescheduled' })}
                    style={{ marginRight: '1rem' }}
                  >
                    Save
                  </button>
                  <button className="black-btn" onClick={() => setRescheduleId(null)} style={{ background: '#ff4d4f' }}>
                    Cancel
                  </button>
                </>
              ) : (
                <>
                  {session.status !== 'completed' && session.status !== 'cancelled' && (
                    <>
                      <button style={sessionListStyles.actionButton} onClick={() => setRescheduleId(session._id)}>
                        Reschedule
                      </button>
                      <button style={sessionListStyles.cancelButton} onClick={() => handleUpdateSession(session._id, { status: 'cancelled' })}>
                        Cancel
                      </button>
                      <button style={sessionListStyles.actionButton} onClick={() => handleUpdateSession(session._id, { status: 'completed' })}>
                        Mark as Completed
                      </button>
                    </>
                  )}
                  {session.status === 'completed' && !reviewedSessionIds.includes(session._id) && (
                    <div style={{ marginTop: '1rem', background: '#181818', padding: '1rem', borderRadius: '8px' }}>
                      {reviewError && <div style={{ color: '#ff4d4f' }}>{reviewError}</div>}
                      {reviewSuccess && <div style={{ color: '#52c41a' }}>{reviewSuccess}</div>}
                      <label>Rating: </label>
                      <select style={sessionListStyles.input} value={reviewRating} onChange={(e) => setReviewRating(Number(e.target.value))}>
                        {[5, 4, 3, 2, 1].map((val) => (
                          <option key={val} value={val}>{val}</option>
                        ))}
                      </select>
                      <textarea
                        style={sessionListStyles.input}
                        placeholder="Write your review..."
                        value={reviewComment}
                        onChange={(e) => setReviewComment(e.target.value)}
                        rows={3}
                      />
                      <button style={sessionListStyles.actionButton} onClick={() => handleReviewSubmit(session)}>
                        Submit Review
                      </button>
                    </div>
                  )}
                  {session.status === 'completed' && reviewedSessionIds.includes(session._id) && (
                    <div style={{ marginTop: '1rem', color: '#52c41a' }}>Reviewed âœ…</div>
                  )}
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SessionList;
