import React, { useEffect, useState } from 'react';

const sentRequestsStyles = {
  container: {
    minHeight: '100vh',
    background: '#111',
    color: '#fff',
    padding: '2rem',
  },
  card: {
    background: '#222',
    borderRadius: '8px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.5)',
    padding: '1.5rem',
    marginBottom: '1.5rem',
    maxWidth: '600px',
    marginLeft: 'auto',
    marginRight: 'auto',
    position: 'relative',
  },
  title: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem',
  },
  meta: {
    color: '#aaa',
    fontSize: '0.95rem',
    marginBottom: '0.5rem',
  },
  status: {
    fontWeight: 'bold',
    marginTop: '0.5rem',
    color: '#0d6efd',
  },
  cancelButton: {
    background: '#ff4d4f',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginTop: '1rem',
  },
};

function SentRequests() {
  const [sentRequests, setSentRequests] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  const fetchSentRequests = async () => {
    setIsLoading(true);
    setErrorMessage('');
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to view sent requests.');
      setIsLoading(false);
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/skill-requests/sent', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) throw new Error('Failed to fetch sent requests');
      const data = await response.json();
      setSentRequests(data);
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchSentRequests();
    // eslint-disable-next-line
  }, []);

  const handleCancelRequest = async (requestId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to cancel requests.');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/skill-requests/${requestId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status: 'cancelled' }),
      });
      if (!response.ok) throw new Error('Failed to cancel request');
      // Refresh requests
      fetchSentRequests();
    } catch (err) {
      setErrorMessage(err.message);
    }
  };

  return (
    <div style={sentRequestsStyles.container}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Sent Skill Requests</h2>
      {isLoading && <div>Loading...</div>}
      {errorMessage && <div style={{ color: '#ff4d4f', textAlign: 'center' }}>{errorMessage}</div>}
      {sentRequests.map(request => (
        <div key={request._id} style={sentRequestsStyles.card}>
          <div style={sentRequestsStyles.title}>Skill: {request.skill?.title}</div>
          <div style={sentRequestsStyles.meta}>
            To: {request.owner?.name} ({request.owner?.email})<br />
            Message: {request.message}<br />
            Preferred Schedule: {request.preferredSchedule}
          </div>
          <div style={sentRequestsStyles.status}>Status: {request.status.charAt(0).toUpperCase() + request.status.slice(1)}</div>
          {request.status === 'pending' && (
            <button
              style={sentRequestsStyles.cancelButton}
              onClick={() => handleCancelRequest(request._id)}
            >
              Cancel
            </button>
          )}
        </div>
      ))}
      {!isLoading && sentRequests.length === 0 && !errorMessage && (
        <div style={{ textAlign: 'center', color: '#aaa' }}>No sent requests.</div>
      )}
    </div>
  );
}

export default SentRequests;
