import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const skillFormStyles = {
  container: {
    minHeight: '100vh',
    background: '#111',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  form: {
    background: '#222',
    padding: '2rem',
    borderRadius: '8px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.5)',
    width: '100%',
    maxWidth: '400px',
    color: '#fff',
  },
  input: {
    width: '100%',
    padding: '0.75rem',
    margin: '0.5rem 0',
    borderRadius: '4px',
    border: '1px solid #444',
    background: '#181818',
    color: '#fff',
    fontSize: '1rem',
  },
  select: {
    width: '100%',
    padding: '0.75rem',
    margin: '0.5rem 0',
    borderRadius: '4px',
    border: '1px solid #444',
    background: '#181818',
    color: '#fff',
    fontSize: '1rem',
  },
  button: {
    width: '100%',
    padding: '0.75rem',
    background: '#0d6efd',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    fontWeight: 'bold',
    fontSize: '1rem',
    marginTop: '1rem',
    cursor: 'pointer',
  },
  error: {
    color: '#ff4d4f',
    marginBottom: '1rem',
    textAlign: 'center',
  },
  success: {
    color: '#52c41a',
    marginBottom: '1rem',
    textAlign: 'center',
  },
};

function CreateSkill() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [type, setType] = useState('offer');
  const [availability, setAvailability] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  const handleSkillSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');
    setIsSubmitting(true);
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to create a skill listing.');
      setIsSubmitting(false);
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/skills', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          title,
          description,
          category,
          type,
          availability: availability.split(',').map(a => a.trim()).filter(Boolean),
        }),
      });
      if (!response.ok) throw new Error('Failed to create skill listing');
      setSuccessMessage('Skill listing created successfully!');
      setTitle('');
      setDescription('');
      setCategory('');
      setType('offer');
      setAvailability('');
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={skillFormStyles.container}>
      <form style={skillFormStyles.form} onSubmit={handleSkillSubmit}>
        <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>Create Skill Listing</h2>
        {errorMessage && <div style={skillFormStyles.error}>{errorMessage}</div>}
        {successMessage && <div style={skillFormStyles.success}>{successMessage}</div>}
        <input
          style={skillFormStyles.input}
          type="text"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
        <textarea
          style={skillFormStyles.input}
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
          rows={3}
        />
        <input
          style={skillFormStyles.input}
          type="text"
          placeholder="Category (e.g. Music, Tech, Language)"
          value={category}
          onChange={e => setCategory(e.target.value)}
          required
        />
        <select
          style={skillFormStyles.select}
          value={type}
          onChange={e => setType(e.target.value)}
        >
          <option value="offer">Offer</option>
          <option value="request">Request</option>
        </select>
        <input
          style={skillFormStyles.input}
          type="text"
          placeholder="Availability (comma separated, e.g. Monday 10am-12pm, Friday 2pm-4pm)"
          value={availability}
          onChange={e => setAvailability(e.target.value)}
        />
        <button style={skillFormStyles.button} type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Creating...' : 'Create Skill'}
        </button>
      </form>
    </div>
  );
}

export default CreateSkill;
