import React, { useEffect, useState } from 'react';

const styles = {
  container: {
    minHeight: '100vh',
    background: '#111',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  card: {
    background: '#222',
    padding: '2rem',
    borderRadius: '8px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.5)',
    width: '100%',
    maxWidth: '400px',
    color: '#fff',
  },
  input: {
    width: '100%',
    padding: '0.75rem',
    margin: '0.5rem 0',
    borderRadius: '4px',
    border: '1px solid #444',
    background: '#181818',
    color: '#fff',
    fontSize: '1rem',
  },
  button: {
    width: '100%',
    padding: '0.75rem',
    background: '#0d6efd',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    fontWeight: 'bold',
    fontSize: '1rem',
    marginTop: '1rem',
    cursor: 'pointer',
  },
  label: {
    fontWeight: 'bold',
    marginTop: '1rem',
    display: 'block',
  },
  error: {
    color: '#ff4d4f',
    marginBottom: '1rem',
    textAlign: 'center',
  },
  success: {
    color: '#52c41a',
    marginBottom: '1rem',
    textAlign: 'center',
  },
};

function Profile() {
  const [user, setUser] = useState(null);
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      setError('');
      const token = localStorage.getItem('token');
      if (!token) return setError('Not authenticated');
      try {
        const res = await fetch('http://localhost:5001/api/user/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error('Failed to fetch profile');
        const data = await res.json();
        setUser(data);
        setName(data.name);
        // Fetch reviews for this user
        const reviewsRes = await fetch(`http://localhost:5001/api/reviews/user/${data._id}`);
        if (reviewsRes.ok) {
          const reviewsData = await reviewsRes.json();
          setReviews(reviewsData);
          if (reviewsData.length > 0) {
            const avg = reviewsData.reduce((sum, r) => sum + r.rating, 0) / reviewsData.length;
            setAverageRating(avg.toFixed(2));
          } else {
            setAverageRating(null);
          }
        }
      } catch (err) {
        setError(err.message);
      }
    };
    fetchProfile();
  }, []);

  const handleUpdate = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Not authenticated');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('http://localhost:5001/api/user/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name, password: password || undefined }),
      });
      if (!res.ok) throw new Error('Failed to update profile');
      const data = await res.json();
      setUser(data);
      setSuccess('Profile updated successfully!');
      setPassword('');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>{error ? error : 'Loading profile...'}</div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <form style={styles.card} onSubmit={handleUpdate}>
        <h2 style={{ textAlign: 'center', marginBottom: '1rem' }}>User Profile</h2>
        {error && <div style={styles.error}>{error}</div>}
        {success && <div style={styles.success}>{success}</div>}
        <label style={styles.label}>Name</label>
        <input
          style={styles.input}
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <label style={styles.label}>Email</label>
        <input
          style={styles.input}
          type="email"
          value={user.email}
          disabled
        />
        <label style={styles.label}>Role</label>
        <input
          style={styles.input}
          type="text"
          value={user.role}
          disabled
        />
        <label style={styles.label}>New Password</label>
        <input
          style={styles.input}
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          placeholder="Leave blank to keep current password"
        />
        <button style={styles.button} type="submit" disabled={loading}>
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
        {/* Reviews Section */}
        <div style={{ marginTop: '2rem', background: '#181818', padding: '1rem', borderRadius: '8px' }}>
          <h3 style={{ margin: 0, marginBottom: '0.5rem' }}>Reviews & Ratings</h3>
          {averageRating ? (
            <div style={{ marginBottom: '0.5rem', color: '#ffd700' }}>Average Rating: {averageRating} / 5</div>
          ) : (
            <div style={{ marginBottom: '0.5rem', color: '#aaa' }}>No reviews yet.</div>
          )}
          {reviews.map((review) => (
            <div key={review._id} style={{ marginBottom: '1rem', borderBottom: '1px solid #333', paddingBottom: '0.5rem' }}>
              <div style={{ color: '#ffd700' }}>Rating: {review.rating} / 5</div>
              <div style={{ color: '#fff' }}>{review.comment}</div>
              <div style={{ color: '#aaa', fontSize: '0.95rem' }}>By: {review.reviewer?.name} ({review.reviewer?.email})</div>
            </div>
          ))}
        </div>
      </form>
    </div>
  );
}

export default Profile;
