import React, { useEffect, useState } from 'react';

const receivedRequestsStyles = {
  container: {
    minHeight: '100vh',
    background: '#111',
    color: '#fff',
    padding: '2rem',
  },
  card: {
    background: '#222',
    borderRadius: '8px',
    boxShadow: '0 2px 16px rgba(0,0,0,0.5)',
    padding: '1.5rem',
    marginBottom: '1.5rem',
    maxWidth: '600px',
    marginLeft: 'auto',
    marginRight: 'auto',
    position: 'relative',
  },
  title: {
    fontSize: '1.1rem',
    fontWeight: 'bold',
    marginBottom: '0.5rem',
  },
  meta: {
    color: '#aaa',
    fontSize: '0.95rem',
    marginBottom: '0.5rem',
  },
  status: {
    fontWeight: 'bold',
    marginTop: '0.5rem',
    color: '#0d6efd',
  },
  actionButton: {
    background: '#0d6efd',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginRight: '1rem',
    marginTop: '1rem',
  },
  rejectButton: {
    background: '#ff4d4f',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '0.5rem 1rem',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginTop: '1rem',
  },
};

function ReceivedRequests() {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  const fetchReceivedRequests = async () => {
    setIsLoading(true);
    setErrorMessage('');
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to view received requests.');
      setIsLoading(false);
      return;
    }
    try {
      const response = await fetch('http://localhost:5001/api/skill-requests/received', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) throw new Error('Failed to fetch received requests');
      const data = await response.json();
      setReceivedRequests(data);
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchReceivedRequests();
    // eslint-disable-next-line
  }, []);

  const handleUpdateStatus = async (requestId, newStatus) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setErrorMessage('You must be logged in to update requests.');
      return;
    }
    try {
      const response = await fetch(`http://localhost:5001/api/skill-requests/${requestId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status: newStatus }),
      });
      if (!response.ok) throw new Error('Failed to update request status');
      // Refresh requests
      fetchReceivedRequests();
    } catch (err) {
      setErrorMessage(err.message);
    }
  };

  return (
    <div style={receivedRequestsStyles.container}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>Received Skill Requests</h2>
      {isLoading && <div>Loading...</div>}
      {errorMessage && <div style={{ color: '#ff4d4f', textAlign: 'center' }}>{errorMessage}</div>}
      {receivedRequests.map(request => (
        <div key={request._id} style={receivedRequestsStyles.card}>
          <div style={receivedRequestsStyles.title}>Skill: {request.skill?.title}</div>
          <div style={receivedRequestsStyles.meta}>
            From: {request.requester?.name} ({request.requester?.email})<br />
            Message: {request.message}<br />
            Preferred Schedule: {request.preferredSchedule}
          </div>
          <div style={receivedRequestsStyles.status}>Status: {request.status.charAt(0).toUpperCase() + request.status.slice(1)}</div>
          {request.status === 'pending' && (
            <>
              <button
                style={receivedRequestsStyles.actionButton}
                onClick={() => handleUpdateStatus(request._id, 'accepted')}
              >
                Accept
              </button>
              <button
                style={receivedRequestsStyles.rejectButton}
                onClick={() => handleUpdateStatus(request._id, 'rejected')}
              >
                Reject
              </button>
            </>
          )}
        </div>
      ))}
      {!isLoading && receivedRequests.length === 0 && !errorMessage && (
        <div style={{ textAlign: 'center', color: '#aaa' }}>No received requests.</div>
      )}
    </div>
  );
}

export default ReceivedRequests;
